package de.fu_berlin.imp.apiua.server.java_ee.persistence.db;import java.io.Serializable;import org.apache.log4j.Logger;import com.vaadin.data.util.BeanItemContainer;import de.fu_berlin.imp.apiua.server.java_ee.AppData;import de.fu_berlin.imp.apiua.server.java_ee.model.Doclog;import de.fu_berlin.imp.apiua.server.java_ee.model.DoclogRecord;import de.fu_berlin.imp.apiua.server.java_ee.model.IIdentifier;import de.fu_berlin.imp.apiua.server.java_ee.persistence.MappingDoclogPersistence;import de.fu_berlin.imp.apiua.server.java_ee.utils.LocationUtils;public class DoclogRecordContainer extends BeanItemContainer<DoclogRecord>		implements Serializable {	private static final long serialVersionUID = -5893691842287912978L;	private static final Logger LOGGER = Logger			.getLogger(DoclogRecordContainer.class);	public static DoclogRecordContainer create() {		DoclogRecordContainer container;		try {			MappingDoclogPersistence p = LocationUtils					.getDoclogPersistence(AppData.getServletContext());			container = new DoclogRecordContainer();			for (IIdentifier identifier : p.getIdentifiers()) {				container.addAll(p.getDoclog(identifier));			}			return container;		} catch (Exception e) {			LOGGER.error("Error getting " + Doclog.class.getSimpleName() + "s",					e);		}		return null;	}	public DoclogRecordContainer() throws InstantiationException,			IllegalAccessException {		super(DoclogRecord.class);	}}